import React, { Component } from "react";
import API from "../utils/api";
import List from "./Cards/List";
import Navbar from "./Navbar/Navbar";




class Main extends Component {
    state = {
        users: [],
        search: "",
        searchFirst: "",
        results: [],
        error: "" 
    };

    // When the component mounts, get a list of all available users and update this.state.users
    componentDidMount() {
        API.getRandomUsers()
            .then(res => {
                this.setState({ users: res.data.results })
                console.log(res.data.results)
            })
            .catch(err => console.log(err));
    }

    handleChange = (e) => {
        this.setState({ search: e.target.value });
      };

    handleChangeFirst = (e) => {
        this.setState({ searchFirst: e.target.value });
      };


    render() {
        const { users, search, searchFirst } = this.state;

        const filteredUsers = users.filter((user) =>
        user.name.last.toLowerCase().includes(search.toLowerCase())
      );

      const filteredUsersFirst = users.filter((user) =>
        user.name.first.toLowerCase().includes(searchFirst.toLowerCase())
      );

        return (
            <div>
                <Navbar 
                handleChange={this.handleChange}
                handleChangeFirst={this.handleChangeFirst}/>
                <List users={filteredUsers, filteredUsersFirst}/>
            </div>
        );
    }
}

export default Main;
